package com.ccmsd.hackerRank;

import org.junit.Assert;
import org.junit.Test;


public class SherlockAndTheValidStringTest {

    @Test
    public void Test1() {
        Assert.assertEquals("NO", SherlockAndTheValidString.isValid("aabbcd"));
    }

    @Test
    public void Test2() {
        Assert.assertEquals("NO", SherlockAndTheValidString.isValid("aabbccddeefghi"));
    }

    @Test
    public void Test3() {
        Assert.assertEquals("NO", SherlockAndTheValidString.isValid("aaaabbcc"));
    }
    @Test
    public void Test4() {
        Assert.assertEquals("YES", SherlockAndTheValidString.isValid(""));
    }
}
